definition.script:
pipeline{
    agent any
    stages{
        stage("checkout the code from github"){
            steps{
                git url:'https://github.com/shankargouda1/DevOpsClassCodes'
            }
        }
        stage("code compile"){
            steps{
                sh "mvn compile"
                echo "compiling is done"
            }
        }
        stage("parallel stages"){
            parallel{
                stage("code testing"){
                    steps{
                        sh "mvn test"
                    }
                }    
                stage("code qa"){
                    steps{
                        sh "mvn pmd:pmd"
                         recordIssues sourceCodeRetention: 'LAST_BUILD', tools: [pmdParser()]
                    }        
                }    
            }        
        }
        stage("code package"){
            steps{
                sh "mvn package"
            }
        }
        stage("deploy"){
            steps{
                sh "sudo cp -f /var/lib/jenkins/workspace/declarative-pipeline/target/addressbook.war /opt/tomcat/webapps/"
           }
        }
    }    
}
