definition.script:
def sample_array = [ "TOM","JACK","HARRY" ]
pipeline{
    agent any
    stages{
        stage("print the array"){
            steps{
                echo "${sample_array}"
            }
        }
        stage("print single array"){
            steps{
                echo "${sample_array[1]}"
            }
        }
        stage("print individually"){
            steps{
                script{
                    for (i = 0;i < sample_array.size(); i++){
                        echo "${sample_array[i]}"
                    }
                }
            }
        }
    }
}
