definition.script:
def sample_array = [ "mvn compile", "mvn test" ]
pipeline{
    agent any
    stages{
        stage("checkout the code from github"){
            steps{
                git url:'https://github.com/shankargouda1/cicdakshat'
            }
        }
        stage("code compile"){
            steps{
                sh "${sample_array[0]}"
                echo "compiling is done"
            }
        }
        stage("code testing"){
            steps{
                sh "${sample_array[1]}"
            }    
        }        
    }
}
