definition.script:
pipeline{
    environment {
        FNAME = "RAJU"
        LNAME = "RAMU"
    }
    agent any
    stages{
        stage("calling variables"){
            steps{
                 sh "echo hello ${FNAME} ${LNAME}"
            }
        }
        stage("changing name of FNAME"){
            environment {
            FNAME = "PINTO"
            }       
            steps{
                sh "echo ${FNAME} ${LNAME}"
            }      
        }
        stage("calling names"){
            steps{
                sh "echo ${FNAME} ${LNAME}"
            }
        }
    }   
}
